mapper.select:
select
,
from t_menu_info where id in (select m.menu_id from t_role_menu_info m where m.role_id in (select ur.role_id from t_user_role_info ur where ur.user_id = #{id,jdbcType=INTEGER})) and belong = #{flag,jdbcType=INTEGER}

select
,
from t_menu_info where id in (select m.menu_id from t_customer_role_menu_info m where m.role_id in (select ur.role_id from t_customer_user_role_info ur where ur.user_id = #{id,jdbcType=INTEGER})) and belong = #{flag,jdbcType=INTEGER}

select

,

from t_menu_info


        and id = #{id,jdbcType=INTEGER}


        and parent_id = #{parentId,jdbcType=INTEGER}


        and name = #{name,jdbcType=VARCHAR}


        and seq = #{seq,jdbcType=INTEGER}


        and level = #{level,jdbcType=INTEGER}


        and type = #{type,jdbcType=INTEGER}


        and belong = #{belong,jdbcType=INTEGER}


        and icon = #{icon,jdbcType=VARCHAR}


        and remark = #{remark,jdbcType=VARCHAR}


        and create_time = #{createTime,jdbcType=TIMESTAMP}


        and create_by = #{createBy,jdbcType=VARCHAR}


        and last_update_time = #{lastUpdateTime,jdbcType=TIMESTAMP}


        and last_update_by = #{lastUpdateBy,jdbcType=VARCHAR}


        and version = #{version,jdbcType=INTEGER}


        and url = #{url,jdbcType=LONGVARCHAR}

select
count(1)
from t_menu_info
where
name = #{name,jdbcType=VARCHAR}
and
parent_id = #{parentId,jdbcType=INTEGER}
and
belong = #{belong,jdbcType=INTEGER}
